  margin-right: auto;
  margin-left: auto;
  padding-right: 2rem;
  padding-left: 2rem;
}
@media (min-width: 1400px){
  .container{
    max-width: 1400px;
  }
}
/* Glass morphism utilities */
.glass {
    background: rgba(255, 255, 255, 0.05);
    backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 255, 255, 0.1);
  }
.glass-card {
    background: rgba(255, 255, 255, 0.12);
    backdrop-filter: blur(20px);
    border: 1px solid rgba(255, 255, 255, 0.15);
    box-shadow: 
      0 8px 32px 0 rgba(31, 38, 135, 0.15),
      0 1px 1px 0 rgba(255, 255, 255, 0.25) inset,
      0 -1px 1px 0 rgba(0, 0, 0, 0.05) inset;
  }
.dark .glass {
    background: rgba(255, 255, 255, 0.02);
    backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 255, 255, 0.05);
  }
.dark .glass-strong {
    background: rgba(255, 255, 255, 0.05);
    backdrop-filter: blur(20px);
    border: 1px solid rgba(255, 255, 255, 0.1);
  }
.dark .glass-card {
    background: rgba(255, 255, 255, 0.05);
    backdrop-filter: blur(20px);
    border: 1px solid rgba(255, 255, 255, 0.1);
    box-shadow: 
      0 8px 32px 0 rgba(0, 0, 0, 0.4),
      0 1px 1px 0 rgba(255, 255, 255, 0.1) inset,
      0 -1px 1px 0 rgba(0, 0, 0, 0.2) inset;
  }
/* Custom gradient backgrounds */
.bg-gradient-primary {
    background: linear-gradient(135deg, hsl(var(--brand-500)) 0%, hsl(var(--brand-700)) 100%);
  }
/* Custom scrollbar */
.custom-scrollbar::-webkit-scrollbar {
    width: 6px;
    height: 6px;
  }
.custom-scrollbar::-webkit-scrollbar-track {
    background: transparent;
  }
.custom-scrollbar::-webkit-scrollbar-thumb {
    background: hsl(var(--gray-300));
    border-radius: 3px;
    -webkit-transition: background-color 0.2s ease;
    transition: background-color 0.2s ease;
  }
.custom-scrollbar::-webkit-scrollbar-thumb:hover {
    background: hsl(var(--gray-400));
  }
.dark .custom-scrollbar::-webkit-scrollbar-thumb {
    background: hsl(var(--gray-600));
  }
.dark .custom-scrollbar::-webkit-scrollbar-thumb:hover {
    background: hsl(var(--gray-500));
  }
/* Shimmer loading effect */
.dark .shimmer::before {
    background: linear-gradient(
      90deg,
      transparent,
      rgba(255, 255, 255, 0.1),
      transparent
    );
  }
/* Button hover glow effects */
/* Status indicators */
.status-dot{
  position: relative;
  display: inline-flex;
  height: 0.5rem;
  width: 0.5rem;
  border-radius: 9999px;
}
.status-dot::before{
  position: absolute;
  display: inline-flex;
  height: 100%;
  width: 100%;
}
@keyframes ping{
  75%, 100%{
    transform: scale(2);
    opacity: 0;
  }
}
.status-dot::before{
  animation: ping 1s cubic-bezier(0, 0, 0.2, 1) infinite;
  border-radius: 9999px;
  opacity: 0.75;
    content: '';
    background-color: inherit;
}
.status-dot::after{
  position: relative;
  display: inline-flex;
  height: 0.5rem;
  width: 0.5rem;
  border-radius: 9999px;
    content: '';
    background-color: inherit;
}
/* Enhanced animations */
@keyframes fadeInUp {
    from {
      opacity: 0;
      transform: translateY(30px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }
@keyframes scaleIn {
    from {
      opacity: 0;
      transform: scale(0.9);
    }
    to {
      opacity: 1;
      transform: scale(1);
    }
  }
@keyframes float {
    0%, 100% {
      transform: translateY(0px);
    }
    50% {
      transform: translateY(-10px);
    }
  }
@keyframes pulse-glow {
    0%, 100% {
      box-shadow: 0 0 20px rgba(59, 130, 246, 0.4);
    }
    50% {
      box-shadow: 0 0 40px rgba(59, 130, 246, 0.8);
    }
  }
/* Enhanced card styles */
/* Enhanced shadow utilities */
/* Interactive elements */
.interactive {
    transition: all 0.2s ease;
    cursor: pointer;
  }
.interactive:hover {
    transform: translateY(-2px);
  }
.interactive:active {
    transform: translateY(0);
  }
/* Modern loading spinner */
.spinner {
    width: 40px;
    height: 40px;
    border: 4px solid rgba(59, 130, 246, 0.1);
    border-left-color: #3b82f6;
    border-radius: 50%;
    animation: spin 1s linear infinite;
  }
/* Gradient text effects */
.sr-only{
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
