/* Status indicators */
.status-dot{
  position: relative;
  display: inline-flex;
  height: 0.5rem;
  width: 0.5rem;
  border-radius: 9999px;
}
.status-dot::before{
  position: absolute;
  display: inline-flex;
  height: 100%;
  width: 100%;
}
@keyframes ping{
  75%, 100%{
    transform: scale(2);
    opacity: 0;
  }
}
.status-dot::before{
  animation: ping 1s cubic-bezier(0, 0, 0.2, 1) infinite;
  border-radius: 9999px;
  opacity: 0.75;
    content: '';
    background-color: inherit;
}
.status-dot::after{
  position: relative;
  display: inline-flex;
  height: 0.5rem;
  width: 0.5rem;
  border-radius: 9999px;
    content: '';
    background-color: inherit;
}
