  color: var(--navy);
  border: 1px solid var(--navy);
  font-weight: 300;
  letter-spacing: 1px;
  text-transform: uppercase;
  font-size: 0.7rem;
  padding: 0.25rem 0.75rem;
  transition: all 0.3s ease;
}
.badge-elegant:hover {
  background: var(--navy);
  color: var(--white);
}
.badge-gold {
  border-color: var(--gold);
  color: var(--gold);
}
.badge-gold:hover {
  background: var(--gold);
  color: var(--navy);
}
.badge-gray {
  border-color: var(--gray-elegant);
  color: var(--gray-elegant);
}
.badge-gray:hover {
  background: var(--gray-elegant);
  color: var(--white);
}
/* Text Colors */
.text-navy {
  color: var(--navy) !important;
}
.text-navy-dark {
  color: var(--navy-dark) !important;
}
.text-gold {
  color: var(--gold) !important;
}
.text-gray-elegant {
  color: var(--gray-elegant) !important;
}
.text-gray-light {
  color: var(--gray-light) !important;
}
/* Background Colors */
.bg-gray-light {
  background-color: var(--gray-light) !important;
}
.bg-gold-light {
  background-color: var(--gold-light) !important;
}
.bg-navy {
  background-color: var(--navy) !important;
}
.bg-white {
  background-color: var(--white) !important;
}
/* Border Colors */
.border-gold {
  border-color: var(--gold) !important;
}
.border-gray-elegant {
  border-color: var(--gray-elegant) !important;
}
/* Executive Buttons */
.btn-executive {
  background: var(--navy);
  color: var(--white);
  border: none;
  font-weight: 300;
  letter-spacing: 1px;
  text-transform: uppercase;
  font-size: 0.875rem;
  padding: 0.75rem 2rem;
  transition: all 0.3s ease;
  position: relative;
  overflow: hidden;
}
.btn-executive::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: var(--gold);
  transition: left 0.3s ease;
}
.btn-executive:hover::before {
  left: 0;
}
.btn-executive span {
  position: relative;
  z-index: 1;
}
.btn-outline-executive {
  background: transparent;
  color: var(--navy);
  border: 1px solid var(--navy);
  font-weight: 300;
  letter-spacing: 1px;
  text-transform: uppercase;
  font-size: 0.875rem;
  padding: 0.75rem 2rem;
  transition: all 0.3s ease;
}
.btn-outline-executive:hover {
  background: var(--navy);
  color: var(--white);
}
/* Tabs - Executive Style */
.tabs-executive {
  border-bottom: 1px solid var(--gray-light);
  background: transparent;
}
.tab-executive {
  background: transparent;
  color: var(--gray-elegant);
  border: none;
  border-bottom: 2px solid transparent;
  font-weight: 300;
  letter-spacing: 0.5px;
  text-transform: uppercase;
  font-size: 0.875rem;
  padding: 1rem 1.5rem;
  transition: all 0.3s ease;
  position: relative;
}
.tab-executive:hover {
  color: var(--navy);
}
.tab-executive.active,
.tab-executive[data-state="active"] {
  color: var(--navy);
  border-bottom-color: var(--gold);
}
.tab-executive.active::after,
.tab-executive[data-state="active"]::after {
  content: '';
  position: absolute;
  bottom: -1px;
  left: 50%;
  transform: translateX(-50%);
  width: 0;
  height: 0;
  border-left: 6px solid transparent;
  border-right: 6px solid transparent;
  border-bottom: 6px solid var(--gold);
}
/* Value Display - Elegant */
.value-display {
  position: relative;
  display: inline-block;
}
.value-label {
  color: var(--gray-elegant);
  font-size: 0.75rem;
  text-transform: uppercase;
  letter-spacing: 1px;
  font-weight: 300;
  margin-bottom: 0.25rem;
}
.value-amount {
  color: var(--navy);
  font-size: 1.75rem;
  font-weight: 300;
  font-family: 'Georgia', serif;
}
.value-amount.gold {
  color: var(--gold);
}
/* Subtle Animations */
@keyframes fadeInElegant {
  from {
    opacity: 0;
    transform: translateY(10px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}
.animate-elegant {
  animation: fadeInElegant 0.6s ease-out;
}
/* Property Grid - Clean Layout */
.property-grid {
  display: grid;
  grid-template-columns: 1fr;
  gap: 1px;
  background: var(--gray-light);
  border: 1px solid var(--gray-light);
}
.property-grid-item {
  background: var(--white);
  padding: 1rem;
  transition: background 0.3s ease;
}
.property-grid-item:hover {
  background: var(--gold-light);
}
.property-grid-label {
  color: var(--gray-elegant);
  font-size: 0.7rem;
  text-transform: uppercase;
  letter-spacing: 1px;
  margin-bottom: 0.25rem;
}
.property-grid-value {
  color: var(--navy);
  font-size: 1rem;
  font-weight: 400;
}
/* Executive Contact Section */
.contact-section {
  border-top: 1px solid var(--gray-light);
  padding-top: 1.5rem;
}
.contact-input {
  border: none;
  border-bottom: 1px solid var(--gray-light);
  border-radius: 0;
  padding: 0.5rem 0;
  font-weight: 300;
  transition: border-color 0.3s ease;
  background: transparent;
}
.contact-input:focus {
  border-bottom-color: var(--gold);
  outline: none;
  box-shadow: none;
}
/* Status Indicators - Minimal */
.status-dot {
  width: 8px;
  height: 8px;
  border-radius: 50%;
  display: inline-block;
  margin-right: 0.5rem;
}
.status-dot.active {
  background: var(--gold);
}
.status-dot.inactive {
  background: var(--gray-elegant);
}
/* Elegant Scrollbar */
.elegant-scroll::-webkit-scrollbar {
  width: 8px;
}
.elegant-scroll::-webkit-scrollbar-track {
  background: var(--gray-light);
}
.elegant-scroll::-webkit-scrollbar-thumb {
  background: var(--navy);
  border-radius: 0;
}
.elegant-scroll::-webkit-scrollbar-thumb:hover {
  background: var(--navy-dark);
}
/* Print Styles */
@media print {
  .executive-header {
    background: var(--navy);
    -webkit-print-color-adjust: exact;
    print-color-adjust: exact;
  }

  .elegant-card {
    page-break-inside: avoid;
  }

  .no-print {
    display: none !important;
  }
}
/* Responsive Elegance */
@media (min-width: 768px) {
  .property-grid {
    grid-template-columns: repeat(2, 1fr);
  }
}
@media (min-width: 1024px) {
  .property-grid {
    grid-template-columns: repeat(4, 1fr);
  }
}
